<?php//5分header("Content-Type:text/html; charset=utf-8");//date_default_timezone_set('prc');set_time_limit(0); $domain = "http://youzuan.com";//填写网站域名$queues = array(    'home/queue/chart',         //计算行情    'home/queue/tendency',      //计算趋势);for ($i = 0; $i < count($queues); $i++) {print_r($domain . "/" . $queues[$i]);    http_get($domain . "/" . $queues[$i]);}echo "本次执行完毕";function http_get($url){    $oCurl = curl_init();    if (stripos($url, "https://") !== FALSE) {        curl_setopt($oCurl, CURLOPT_SSL_VERIFYPEER, FALSE);        curl_setopt($oCurl, CURLOPT_SSL_VERIFYHOST, FALSE);        curl_setopt($oCurl, CURLOPT_SSLVERSION, 1); //CURL_SSLVERSION_TLSv1    }    curl_setopt($oCurl, CURLOPT_URL, $url);    curl_setopt($oCurl, CURLOPT_RETURNTRANSFER, 1);    $sContent = curl_exec($oCurl);    $aStatus = curl_getinfo($oCurl);    curl_close($oCurl);    if (intval($aStatus["http_code"]) == 200) {        return true;    } else {        return false;    }}function randomFloat($min = 0, $max = 10)  {         $num = $min + mt_rand() / mt_getrandmax() * ($max - $min);  	          return sprintf("%.2f", $num);    }?>