<?php//30分header("Content-Type:text/html; charset=utf-8");//date_default_timezone_set('prc');set_time_limit(0); $domain = "http://youzuan.com";//填写网站域名$queues = array(    'admin/login/queue',        //记录最后执行时间    'home/queue/hou_price',        //日结算最高价格    'home/queue/qianbao',      //同步钱包转入记录    'home/queue/move',         //处理交易状态:正常    //'home/queue/yichang',      //处理交易状态:异常    'home/queue/thaw',        //解冻释放    'home/queue/houprice',        //解冻释放    	'home/queue/shifang',        //平台币释放、定时分红、矿工奖励 		'home/queue/monthjd',        //20%平台币月解冻		'home/queue/zcjljd',        //注册奖励解冻);for ($i = 0; $i < count($queues); $i++) {		    http_get($domain . "/" . $queues[$i]);	}echo "本次执行完毕";function http_get($url){    $oCurl = curl_init();    if (stripos($url, "https://") !== FALSE) {        curl_setopt($oCurl, CURLOPT_SSL_VERIFYPEER, FALSE);        curl_setopt($oCurl, CURLOPT_SSL_VERIFYHOST, FALSE);        curl_setopt($oCurl, CURLOPT_SSLVERSION, 1); //CURL_SSLVERSION_TLSv1    }    curl_setopt($oCurl, CURLOPT_URL, $url);    curl_setopt($oCurl, CURLOPT_RETURNTRANSFER, 1);    $sContent = curl_exec($oCurl);    $aStatus = curl_getinfo($oCurl);    curl_close($oCurl);    if (intval($aStatus["http_code"]) == 200) {        return true;    } else {        return false;    }}function randomFloat($min = 0, $max = 10)  {         $num = $min + mt_rand() / mt_getrandmax() * ($max - $min);  	          return sprintf("%.2f", $num);    }?>